package com.pucp.tel05.laboratorio06;

import android.util.Log;

import androidx.annotation.NonNull;

import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.ListenerRegistration;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.UUID;

/**
 * Repositorio para operaciones CRUD de Tareas en Firestore.
 * Maneja: crear, leer, actualizar y eliminar tareas.
 */
public class TareaRepository {

    private static final String TAG = "TareaRepository";
    private static final String COLLECTION_TAREAS = "tareas";
    private final FirebaseFirestore db;
    private ListenerRegistration tareasListener;

    public TareaRepository() {
        this.db = FirebaseFirestore.getInstance();
    }

    /**
     * Obtener todas las tareas de Firestore con actualizaciones en tiempo real.
     * Se suscribe a cambios en la colección y notifica al callback cada vez que hay cambios.
     * @param callback Retorna lista de tareas
     */
    public void obtenerTodasLasTareas(@NonNull OnTareasLoadedCallback callback) {
        // Si ya hay un listener activo, detenerlo primero
        if (tareasListener != null) {
            tareasListener.remove();
        }

        // Registrar un listener que se dispara cada vez que hay cambios
        tareasListener = db.collection(COLLECTION_TAREAS)
                .addSnapshotListener((querySnapshot, e) -> {
                    if (e != null) {
                        Log.e(TAG, "❌ Error al escuchar tareas: " + e.getMessage());
                        callback.onError(e.getMessage());
                        return;
                    }

                    if (querySnapshot != null) {
                        List<Task> tareas = new ArrayList<>();
                        for (com.google.firebase.firestore.DocumentSnapshot doc : querySnapshot.getDocuments()) {
                            Task tarea = doc.toObject(Task.class);
                            if (tarea != null) {
                                tarea.setId(doc.getId()); // Asignar ID del documento
                                tareas.add(tarea);
                            }
                        }
                        Log.i(TAG, "✅ Tareas cargadas (tiempo real): " + tareas.size());
                        callback.onSuccess(tareas);
                    }
                });
    }

    /**
     * Detener la escucha de cambios en tiempo real.
     * Llamar cuando el fragment/activity se destruya.
     */
    public void detenerEscucha() {
        if (tareasListener != null) {
            tareasListener.remove();
            tareasListener = null;
            Log.i(TAG, "ℹ️ Escucha de tareas detenida");
        }
    }

    /**
     * Crear una nueva tarea en Firestore.
     * @param tarea Objeto tarea a guardar
     * @param callback Callback con ID de la tarea creada
     */
    public void crearTarea(@NonNull Task tarea, @NonNull OnTareaOperationCallback callback) {
        String tareaId = UUID.randomUUID().toString();
        tarea.setId(tareaId);

        Map<String, Object> tareaMap = new HashMap<>();
        tareaMap.put("titulo", tarea.getTitulo());
        tareaMap.put("descripcion", tarea.getDescripcion());
        tareaMap.put("fechaLimite", tarea.getFechaLimite()); // Timestamp se guarda directamente
        tareaMap.put("estado", tarea.isEstado());

        db.collection(COLLECTION_TAREAS)
                .document(tareaId)
                .set(tareaMap)
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea creada: " + tareaId);
                    callback.onSuccess("Tarea registrada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al crear tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    /**
     * Actualizar una tarea existente.
     * @param tarea Objeto tarea con cambios
     * @param callback Callback de operación
     */
    public void actualizarTarea(@NonNull Task tarea, @NonNull OnTareaOperationCallback callback) {
        if (tarea.getId() == null || tarea.getId().isEmpty()) {
            callback.onError("ID de tarea inválido");
            return;
        }

        Map<String, Object> tareaMap = new HashMap<>();
        tareaMap.put("titulo", tarea.getTitulo());
        tareaMap.put("descripcion", tarea.getDescripcion());
        tareaMap.put("fechaLimite", tarea.getFechaLimite()); // Timestamp se guarda directamente
        tareaMap.put("estado", tarea.isEstado());

        db.collection(COLLECTION_TAREAS)
                .document(tarea.getId())
                .update(tareaMap)
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea actualizada: " + tarea.getId());
                    callback.onSuccess("Tarea modificada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al actualizar tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    /**
     * Eliminar una tarea de Firestore.
     * @param tareaId ID de la tarea a eliminar
     * @param callback Callback de operación
     */
    public void eliminarTarea(@NonNull String tareaId, @NonNull OnTareaOperationCallback callback) {
        db.collection(COLLECTION_TAREAS)
                .document(tareaId)
                .delete()
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea eliminada: " + tareaId);
                    callback.onSuccess("Tarea eliminada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al eliminar tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    /**
     * Callback para operaciones de carga de tareas.
     */
    public interface OnTareasLoadedCallback {
        void onSuccess(List<Task> tareas);
        void onError(String errorMessage);
    }

    /**
     * Callback para operaciones CRUD de una tarea.
     */
    public interface OnTareaOperationCallback {
        void onSuccess(String message);
        void onError(String errorMessage);
    }
}

package com.pucp.tel05.laboratorio06;

import android.util.Log;

import androidx.annotation.NonNull;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.ListenerRegistration;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.UUID;

public class TareaRepository {

    private static final String TAG = "TareaRepository";
    private static final String COLLECTION_TAREAS = "tareas";
    private final FirebaseFirestore db;
    private final FirebaseAuth auth;
    private ListenerRegistration tareasListener;

    public TareaRepository() {
        this.db = FirebaseFirestore.getInstance();
        this.auth = FirebaseAuth.getInstance();
    }

    private String getCurrentUserId() {
        if (auth.getCurrentUser() != null) {
            return auth.getCurrentUser().getUid();
        }
        return null;
    }

    public void obtenerTodasLasTareas(@NonNull OnTareasLoadedCallback callback) {
        String userId = getCurrentUserId();
        if (userId == null) {
            callback.onError("Usuario no autenticado");
            return;
        }

        if (tareasListener != null) {
            tareasListener.remove();
        }

        tareasListener = db.collection(COLLECTION_TAREAS)
                .whereEqualTo("userId", userId)
                .addSnapshotListener((querySnapshot, e) -> {
                    if (e != null) {
                        Log.e(TAG, "❌ Error al escuchar tareas: " + e.getMessage());
                        callback.onError(e.getMessage());
                        return;
                    }

                    if (querySnapshot != null) {
                        List<Task> tareas = new ArrayList<>();
                        for (com.google.firebase.firestore.DocumentSnapshot doc : querySnapshot.getDocuments()) {
                            Task tarea = doc.toObject(Task.class);
                            if (tarea != null) {
                                tarea.setId(doc.getId());
                                tareas.add(tarea);
                            }
                        }
                        Log.i(TAG, "✅ Tareas cargadas (tiempo real): " + tareas.size());
                        callback.onSuccess(tareas);
                    }
                });
    }

    public void detenerEscucha() {
        if (tareasListener != null) {
            tareasListener.remove();
            tareasListener = null;
            Log.i(TAG, "ℹ️ Escucha de tareas detenida");
        }
    }

    public void crearTarea(@NonNull Task tarea, @NonNull OnTareaOperationCallback callback) {
        String userId = getCurrentUserId();
        if (userId == null) {
            callback.onError("Usuario no autenticado");
            return;
        }

        String tareaId = UUID.randomUUID().toString();
        tarea.setId(tareaId);
        tarea.setUserId(userId);

        Map<String, Object> tareaMap = new HashMap<>();
        tareaMap.put("userId", userId);
        tareaMap.put("titulo", tarea.getTitulo());
        tareaMap.put("descripcion", tarea.getDescripcion());
        tareaMap.put("fechaLimite", tarea.getFechaLimite());
        tareaMap.put("estado", tarea.isEstado());

        db.collection(COLLECTION_TAREAS)
                .document(tareaId)
                .set(tareaMap)
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea creada: " + tareaId);
                    callback.onSuccess("Tarea registrada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al crear tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    public void actualizarTarea(@NonNull Task tarea, @NonNull OnTareaOperationCallback callback) {
        if (tarea.getId() == null || tarea.getId().isEmpty()) {
            callback.onError("ID de tarea inválido");
            return;
        }

        Map<String, Object> tareaMap = new HashMap<>();
        tareaMap.put("titulo", tarea.getTitulo());
        tareaMap.put("descripcion", tarea.getDescripcion());
        tareaMap.put("fechaLimite", tarea.getFechaLimite());
        tareaMap.put("estado", tarea.isEstado());

        db.collection(COLLECTION_TAREAS)
                .document(tarea.getId())
                .update(tareaMap)
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea actualizada: " + tarea.getId());
                    callback.onSuccess("Tarea modificada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al actualizar tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    public void eliminarTarea(@NonNull String tareaId, @NonNull OnTareaOperationCallback callback) {
        db.collection(COLLECTION_TAREAS)
                .document(tareaId)
                .delete()
                .addOnSuccessListener(aVoid -> {
                    Log.i(TAG, "✅ Tarea eliminada: " + tareaId);
                    callback.onSuccess("Tarea eliminada correctamente");
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "❌ Error al eliminar tarea: " + e.getMessage());
                    callback.onError("Error: " + e.getMessage());
                });
    }

    public interface OnTareasLoadedCallback {
        void onSuccess(List<Task> tareas);
        void onError(String errorMessage);
    }

    public interface OnTareaOperationCallback {
        void onSuccess(String message);
        void onError(String errorMessage);
    }
}

package com.pucp.tel05.laboratorio06;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import com.pucp.tel05.laboratorio06.databinding.FragmentTareasBinding;

import java.util.ArrayList;
import java.util.List;

public class TareasFragment extends Fragment implements TareaAdapter.OnTareaEditListener {

    private FragmentTareasBinding binding;
    private TareaRepository repository;
    private TareaAdapter adapter;
    private List<Task> tareas = new ArrayList<>();

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        binding = FragmentTareasBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        repository = new TareaRepository();

        // Configurar RecyclerView
        binding.rvTareas.setLayoutManager(new LinearLayoutManager(requireContext()));
        adapter = new TareaAdapter(tareas, repository, this);
        binding.rvTareas.setAdapter(adapter);

        // Botón para agregar tarea
        binding.btnAddTask.setOnClickListener(v -> agregarNuevaTarea());

        // Cargar tareas al iniciar
        cargarTareas();
    }

    /**
     * Cargar todas las tareas de Firestore.
     */
    private void cargarTareas() {
        repository.obtenerTodasLasTareas(new TareaRepository.OnTareasLoadedCallback() {
            @Override
            public void onSuccess(List<Task> tareasCargadas) {
                tareas.clear();
                tareas.addAll(tareasCargadas);
                adapter.notifyDataSetChanged();

                // Mostrar empty state si no hay tareas
                if (tareas.isEmpty()) {
                    binding.tvEmptyState.setVisibility(View.VISIBLE);
                    binding.rvTareas.setVisibility(View.GONE);
                } else {
                    binding.tvEmptyState.setVisibility(View.GONE);
                    binding.rvTareas.setVisibility(View.VISIBLE);
                }
            }

            @Override
            public void onError(String errorMessage) {
                Toast.makeText(requireContext(), "Error: " + errorMessage, Toast.LENGTH_SHORT).show();
            }
        });
    }

    /**
     * Mostrar dialog para agregar una nueva tarea.
     */
    private void agregarNuevaTarea() {
        TareaDialog.mostrarDialogCrearTarea(requireContext(), tarea -> {
            repository.crearTarea(tarea, new TareaRepository.OnTareaOperationCallback() {
                @Override
                public void onSuccess(String message) {
                    Toast.makeText(requireContext(), message, Toast.LENGTH_SHORT).show();
                    cargarTareas(); // Recargar lista
                }

                @Override
                public void onError(String errorMessage) {
                    Toast.makeText(requireContext(), errorMessage, Toast.LENGTH_SHORT).show();
                }
            });
        });
    }

    @Override
    public void onEditTarea(Task tarea) {
        TareaDialog.mostrarDialogEditarTarea(requireContext(), tarea, tareaEditada -> {
            repository.actualizarTarea(tareaEditada, new TareaRepository.OnTareaOperationCallback() {
                @Override
                public void onSuccess(String message) {
                    Toast.makeText(requireContext(), message, Toast.LENGTH_SHORT).show();
                    cargarTareas(); // Recargar lista
                }

                @Override
                public void onError(String errorMessage) {
                    Toast.makeText(requireContext(), errorMessage, Toast.LENGTH_SHORT).show();
                }
            });
        });
    }

    @Override
    public void onTareaChanged() {
        cargarTareas(); // Recargar lista cuando algo cambie
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        // Detener escucha en tiempo real para evitar memory leaks
        if (repository != null) {
            repository.detenerEscucha();
        }
        binding = null;
    }
}

package com.pucp.tel05.laboratorio06;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import com.pucp.tel05.laboratorio06.databinding.FragmentTareasBinding;

import java.util.ArrayList;
import java.util.List;

public class TareasFragment extends Fragment implements TareaAdapter.OnTareaEditListener {

    private FragmentTareasBinding binding;
    private TareaRepository repository;
    private TareaAdapter adapter;
    private List<Task> tareas = new ArrayList<>();

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        binding = FragmentTareasBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        repository = new TareaRepository();

        binding.rvTareas.setLayoutManager(new LinearLayoutManager(requireContext()));
        adapter = new TareaAdapter(tareas, repository, this);
        binding.rvTareas.setAdapter(adapter);

        binding.btnAddTask.setOnClickListener(v -> agregarNuevaTarea());

        cargarTareas();
    }

    private void cargarTareas() {
        repository.obtenerTodasLasTareas(new TareaRepository.OnTareasLoadedCallback() {
            @Override
            public void onSuccess(List<Task> tareasCargadas) {
                tareas.clear();
                tareas.addAll(tareasCargadas);
                adapter.notifyDataSetChanged();

                if (tareas.isEmpty()) {
                    binding.tvEmptyState.setVisibility(View.VISIBLE);
                    binding.rvTareas.setVisibility(View.GONE);
                } else {
                    binding.tvEmptyState.setVisibility(View.GONE);
                    binding.rvTareas.setVisibility(View.VISIBLE);
                }
            }

            @Override
            public void onError(String errorMessage) {
                Toast.makeText(requireContext(), "Error: " + errorMessage, Toast.LENGTH_SHORT).show();
            }
        });
    }

    private void agregarNuevaTarea() {
        TareaDialog.mostrarDialogCrearTarea(requireContext(), tarea -> {
            repository.crearTarea(tarea, new TareaRepository.OnTareaOperationCallback() {
                @Override
                public void onSuccess(String message) {
                    Toast.makeText(requireContext(), message, Toast.LENGTH_SHORT).show();
                    cargarTareas();
                }

                @Override
                public void onError(String errorMessage) {
                    Toast.makeText(requireContext(), errorMessage, Toast.LENGTH_SHORT).show();
                }
            });
        });
    }

    @Override
    public void onEditTarea(Task tarea) {
        TareaDialog.mostrarDialogEditarTarea(requireContext(), tarea, tareaEditada -> {
            repository.actualizarTarea(tareaEditada, new TareaRepository.OnTareaOperationCallback() {
                @Override
                public void onSuccess(String message) {
                    Toast.makeText(requireContext(), message, Toast.LENGTH_SHORT).show();
                    cargarTareas();
                }

                @Override
                public void onError(String errorMessage) {
                    Toast.makeText(requireContext(), errorMessage, Toast.LENGTH_SHORT).show();
                }
            });
        });
    }

    @Override
    public void onTareaChanged() {
        cargarTareas();
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        if (repository != null) {
            repository.detenerEscucha();
        }
        binding = null;
    }
}

package com.pucp.tel05.laboratorio06;

import com.google.firebase.Timestamp;

import java.util.Objects;

/**
 * Modelo simple para una tarea. Mantiene un constructor vacío requerido por Firebase Realtime Database
 * / Cloud Firestore deserializers, además de constructores de conveniencia y setters encadenables.
 */
public class Task {

    private String id;
    private String titulo;
    private String descripcion;
    private Timestamp fechaLimite; // Firestore Timestamp
    private boolean estado;   // false: pendiente, true: completada

    // Required by Firebase deserialization
    public Task() {
    }

    // Convenience constructor (sin id) para crear nuevas tareas antes de asignar un id
    public Task(String titulo, String descripcion, Timestamp fechaLimite, boolean estado) {
        this(null, titulo, descripcion, fechaLimite, estado);
    }

    // Full constructor
    public Task(String id, String titulo, String descripcion, Timestamp fechaLimite, boolean estado) {
        this.id = id;
        this.titulo = titulo;
        this.descripcion = descripcion;
        this.fechaLimite = fechaLimite;
        this.estado = estado;
    }

    // Getters
    public String getId() { return id; }
    public String getTitulo() { return titulo; }
    public String getDescripcion() { return descripcion; }
    public Timestamp getFechaLimite() { return fechaLimite; }
    public boolean isEstado() { return estado; }

    // Fluent setters (devuelven this para encadenar llamadas)
    public Task setId(String id) { this.id = id; return this; }
    public Task setTitulo(String titulo) { this.titulo = titulo; return this; }
    public Task setDescripcion(String descripcion) { this.descripcion = descripcion; return this; }
    public Task setFechaLimite(Timestamp fechaLimite) { this.fechaLimite = fechaLimite; return this; }
    public Task setEstado(boolean estado) { this.estado = estado; return this; }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Task task = (Task) o;
        return fechaLimite == task.fechaLimite && estado == task.estado && Objects.equals(id, task.id) && Objects.equals(titulo, task.titulo) && Objects.equals(descripcion, task.descripcion);
    }

    @Override
    public int hashCode() {
        return Objects.hash(id, titulo, descripcion, fechaLimite, estado);
    }

    @Override
    public String toString() {
        return "Task{" +
                "id='" + id + '\'' +
                ", titulo='" + titulo + '\'' +
                ", descripcion='" + descripcion + '\'' +
                ", fechaLimite=" + fechaLimite +
                ", estado=" + estado +
                '}';
    }
}

package com.pucp.tel05.laboratorio06;

import com.google.firebase.Timestamp;

import java.util.Objects;

public class Task {

    private String id;
    private String userId;
    private String titulo;
    private String descripcion;
    private Timestamp fechaLimite;
    private boolean estado;

    public Task() {
    }

    public Task(String titulo, String descripcion, Timestamp fechaLimite, boolean estado) {
        this(null, null, titulo, descripcion, fechaLimite, estado);
    }

    public Task(String id, String userId, String titulo, String descripcion, Timestamp fechaLimite, boolean estado) {
        this.id = id;
        this.userId = userId;
        this.titulo = titulo;
        this.descripcion = descripcion;
        this.fechaLimite = fechaLimite;
        this.estado = estado;
    }

    public String getId() { return id; }
    public String getUserId() { return userId; }
    public String getTitulo() { return titulo; }
    public String getDescripcion() { return descripcion; }
    public Timestamp getFechaLimite() { return fechaLimite; }
    public boolean isEstado() { return estado; }

    public Task setId(String id) { this.id = id; return this; }
    public Task setUserId(String userId) { this.userId = userId; return this; }
    public Task setTitulo(String titulo) { this.titulo = titulo; return this; }
    public Task setDescripcion(String descripcion) { this.descripcion = descripcion; return this; }
    public Task setFechaLimite(Timestamp fechaLimite) { this.fechaLimite = fechaLimite; return this; }
    public Task setEstado(boolean estado) { this.estado = estado; return this; }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Task task = (Task) o;
        return estado == task.estado && Objects.equals(id, task.id) && Objects.equals(userId, task.userId) && Objects.equals(titulo, task.titulo) && Objects.equals(descripcion, task.descripcion) && Objects.equals(fechaLimite, task.fechaLimite);
    }

    @Override
    public int hashCode() {
        return Objects.hash(id, userId, titulo, descripcion, fechaLimite, estado);
    }

    @Override
    public String toString() {
        return "Task{" +
                "id='" + id + '\'' +
                ", userId='" + userId + '\'' +
                ", titulo='" + titulo + '\'' +
                ", descripcion='" + descripcion + '\'' +
                ", fechaLimite=" + fechaLimite +
                ", estado=" + estado +
                '}';
    }
}
